mapper.select:
select distinct d.discuss_id as discuss_id, discuss_author_id, discuss_title, discuss_comment_number, discuss_tag_id, discuss_state, d.create_time as create_time, d.update_time as update_time, user_name, user_pic_url, tag_name, ( select count(1) from map_user_discuss as m WHERE m.discuss_id = d.discuss_id AND m.user_approve_type = 1
) as approveNumber, (select count(1) from map_user_discuss as m WHERE m.discuss_id = d.discuss_id AND m.user_favorite_type = 1
) as favoriteNumber, ( select m2.user_approve_type from map_user_discuss as m2 WHERE m2.user_id = #{userId,jdbcType=BIGINT} AND m2.discuss_id = d.discuss_id ) as user_approve_type, ( select m3.user_favorite_type from map_user_discuss as m3 WHERE m3.user_id = #{userId,jdbcType=BIGINT} AND m3.discuss_id = d.discuss_id ) as user_favorite_type FROM discuss as d, user as u, tag as t, map_user_discuss as m WHERE u.user_id = d.discuss_author_id AND d.discuss_tag_id = t.tag_id order by d.update_time DESC

